mapper.select:
select * from (select A.*, rownum r from (select * from
bus order by b_no) A)
where r >= #{start} and r <= #{end}

mapper.update:
update bus set b_year = #{b_year}, b_energy = #{b_energy}, 
	b_start = #{b_start}, b_type = #{b_type}, b_acc = #{b_acc},
	b_state = #{b_state}, b_mile = #{b_mile}, b_recent = #{b_recent}
	where b_no = #{b_no}
